  const saveUserHandler = (uname, uage) => {
    setNewUser((prevState) => {
      return [...prevState, { name: uname, age: uage }];
    });
    console.log(newUser)
  };

  return (
    <div>
      <AddUser onSaveUser={saveUserHandler} />
    </div>
  );
}
export default App;
