import { useState } from "react";
import "./ExpenseForm.css";

function ExpenseForm() {
  const [title, setTitleHandler] = useState("");
  const [amountHandler, setAmountHandler] = useState("");
  const [dateHandler, setDateHandler] = useState("");

  const titleChangeHandler = (event) => {
    console.log(event.target.value);
    setTitleHandler(event.target.value);
  };
  const amountChangeHandler = (event) => {
    setAmountHandler(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setDateHandler(event.target.value);
  };
  const submitHandler = () => {
    title:titleHandler;
    amount:amountHandler;
    date: new Date(dateHandler);
  }
  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>title</label>
          <input type="text" onChange={titleChangeHandler}></input>
        </div>
        <div className="new-expense__control">
          <label>amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            onChange={amountChangeHandler}
          ></input>
        </div>
        <div className="new-expense__control">
          <label>date</label>
          <input
            type="date"
            min="01-01-2019"
            max="12-12-2022"
            onChange={dateChangeHandler}
          ></input>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
}

export default ExpenseForm;
