import { useState } from 'react';
import './ExpenseForm.css'

function ExpenseForm(){
    const [titleHandler,setTitleHandler] = useState('');
    const [amountHandler,setAmountHandler] = useState('');
    const [dateHandler,setDateHandler] = useState('');

    return (
<div className='new-expense__controls'>
    <div className='new-expense__control'>
        <label>title</label>
        <input type='text' onChange={titleHandler}></input>
    </div>
    <div className='new-expense__control'>
        <label>amount</label>
        <input type='number' min='0.01' step='0.01' onChange={amountHandler}></input>
    </div>
    <div className='new-expense__control'>
        <label>date</label>
        <input type='date' onChange={dateHandler}></input>
    </div>
</div>

    );
}

export default ExpenseForm;
