import Input from "../../UI/Input";
import classes from "./ealItemForm.module.css";
function MealItemForm(props) {
  return (
    <form>
      <Input
        label="Amount"
        input={{
          id: "amount",
          type: Number,
          min: 1,
          max: 5,
          step: 1,
          defaultValue: 1,
        }}
      />
      <button>+ Add</button>
    </form>
  );
}
export default MealItemForm;
